    private boolean UrlValidation(String url) {
        boolean validated = false;
        final String regex = "(https?|ftp)://(-\\.)?([^\\s/?\\.#-]+\\.?)+(/[^\\s]*)?$"; //https://mathiasbynens.be/demo/url-regex

        if (url != null)
            url = url.trim();

        Pattern pattern = Pattern.compile(regex);
        Matcher matcher = pattern.matcher(url);

        if (matcher.matches()) {
            validated = true;
        }

        return validated;
    }






